mapper.insert:

INSERT INTO t_chat
SET last_msg = NULL,
    iproduct = #{iproduct}

INSERT INTO t_chat_user
SET ichat = #{ichat},
    iuser = #{iuser}

mapper.update:
UPDATE t_chat
SET last_msg = #{msg}
WHERE ichat = #{ichat}

UPDATE t_chat_user
SET istatus = -1
WHERE iuser = #{iuser};

mapper.select:
SELECT iuser, nick, stored_pic AS storedPic
FROM t_user
WHERE iuser = #{iuser}

SELECT istatus
FROM t_chat_user
WHERE ichat = #{ichat} AND iuser != #{loginedIuser};
