mapper.select:
SELECT A.iproduct, A.title, A.stored_pic AS productStoredPic, A.deposit, A.iuser, A.imain_category AS imainCategory, A.isub_category AS isubCategory, C.ipayment, PS.istatus, C.ibuyer, C.price, C.rental_duration AS rentalDuration, C.rental_start_date AS rentalStartDate, C.rental_end_date AS rentalEndDate, C.created_at AS createdAt, D.iuser AS targetIuser, D.nick AS targetNick, D.stored_pic AS userStoredPic FROM t_product A JOIN t_product_payment B ON A.iproduct = B.iproduct LEFT JOIN t_payment C ON B.ipayment = C.ipayment JOIN t_payment_status PS ON C.ipayment = PS.ipayment and PS.istatus != -1 LEFT JOIN t_user D ON
A.iuser = D.iuser where C.ibuyer = #{loginedIuser} AND A.iuser != #{loginedIuser}
C.ibuyer = D.iuser WHERE A.iuser = #{loginedIuser} and C.ibuyer != #{loginedIuser}
GROUP BY C.ipayment ORDER BY C.ipayment DESC LIMIT 0, 10
