mapper.insert:
INSERT INTO review
set
iusers = #{iuser}
,ipayment = #{ipayment}
, contents = #{contents}
, rating = #{rating}

mapper.select:
SELECT COUNT(ireview)
FROM review
WHERE ipayment = #{ipayment}

mapper.update:








update payment_status
set istatus = 1
where ipayment = #{ipayment}

update review
set

    contents = #{contents}


    ,rating = #{rating}

where ireview = #{ireview}

mapper.delete:
DELETE
from review
WHERE ireview = #{ireview}
AND iusers = #{iuser}

mapper.select:
SELECT status
FROM payment_info
WHERE ipayment = #{ipayment} and iuser = #{iuser}

SELECT count(pa.ipayment) as isExists, if(pa.iusers = #{iuser}, 1, 0) as isBuyer
from payment pa
join product P
ON p.iproduct = pa.iproduct AND pa.ipayment = #{ipayment}
WHERE p.iusers = #{iuser} or pa.iusers = #{iuser};

select iusers, ipayment
FROM review
WHERE ireview = #{ireview}

