mapper.insert:
INSERT INTO review
set
iuser = #{iuser}
,ipayment = #{ipayment}
, contents = #{contents}
, rating = #{rating}

mapper.select:
SELECT COUNT(ireview)
FROM review
WHERE ipayment = #{ipayment}

mapper.update:








update payment_status
set istatus = 1
where ipayment = #{ipayment}

update review
set

    contents = #{contents}


    ,rating = #{rating}



where ireview = #{ireview}

mapper.select:
SELECT istatus
FROM payment_status
WHERE ipayment = #{ipayment} and iuser = #{iuser}

select count(PA.ipayment) as isExists, if(PA.ibuyer = #{iuser}, 1, 0) as isBuyer
from product_payment PP
join product P on PP.iproduct = P.iproduct
join payment PA on PP.ipayment = PA.ipayment and PA.ipayment = #{ipayment}
where P.iuser = #{iuser} or PA.ibuyer = #{iuser}

select iuser, ipayment
FROM review
WHERE ireview = #{ireview}

mapper.update:
update user
set rating = #{rating}
where iuser = #{iuser}

mapper.select:
SELECT rating
FROM review
WHERE ireview = #{ireview}
