mapper.insert:
INSERT INTO user
SET addr = #{addr}

    , iauth = #{iauth}

, rest_addr = #{restAddr}
, uid = #{uid}
, upw = #{upw}
, nick = #{nick}
, phone = #{phone}
, email = #{email}
, y = #{y}
, x = #{x}

INSERT INTO comp_info
set comp_code = #{compCode}
, comp_nm = #{compNm}
, iuser = #{iuser}

mapper.update:
