mapper.select:
SELECT uid, upw, auth
FROM users
WHERE phone = #{phone}

mapper.update:
UPDATE users
SET upw = #{upw}
WHERE phone = #{phone}
and uid = #{uid}

UPDATE users us
Left JOIN user u
ON u.iuser = us.iuser
Left JOIN comp c
ON c.iuser = us.iuser


        u.nick =#{nick}


